// import "isomorphic-fetch";

const Home  = ({quotes}) => (

<div id="container">
    <h1>Random Quotes From breaking Bad</h1>
{quotes.map(e => (
  <div className="quote">  
<p>Quote :{e.quote}</p>
<p>Author : {e.author}</p>
</div>
))}
<style jsx> {
`p{
    color:black;
    }
  .quote{
      background:green
  }  
  *{
      margin:0;
      background:black;
      height:100vh;
  }`

} 

</style>
</div>
);
Home.getInitialProps = async () => {
    const response = await fetch('https://breaking-bad-quotes.herokuapp.com/v1/quotes');
    const quotes = await response.json();
    console.log(quotes);
    return{quotes};

};



export default Home
