import React from 'react';
// import "isomorphic-fetch";

const Home  = ({quotes}) => (

<div id="container">
    <h1>Random Quotes From breaking Bad</h1>
{quotes.map(e => (
  <div id="quotes">  
<p>Quote :{e.quote}</p>
<p>Author : {e.author}</p>
</div>
))}
<style jsx>  {
`p , h1{
    color:white;
    }
  #quotes{
      width:100%;
      height:100%;
  }  

  *{
      background:black;
      width:100%
  }
  #container{
      display:flex;
      text-align:center;
      flex-direction:column;
  }`

} 

</style>
</div>
);
Home.getInitialProps = async () => {
    const response = await fetch('https://breaking-bad-quotes.herokuapp.com/v1/quotes');
    const quotes = await response.json();
    console.log(quotes);
    return{quotes};

};



export default Home
